import java.awt.BorderLayout;
import java.io.BufferedReader;
import java.io.File;
import java.io.FileReader;

import javax.swing.JFrame;

import org.apache.commons.cli.*;

import java.util.ArrayList;

public class LogFilePlayer {
	private static String logFilePath = "tempPath";
	private static boolean debug = false;

	private static ArrayList<DataPoint> dataMap = new ArrayList<DataPoint>();

	public static void main(String[] args) {
		parseCLI(args);
		
		File file = new File(logFilePath);
		
		try {
			boolean first = true;
			Long baseTime = (long) 0;
			BufferedReader reader = new BufferedReader(new FileReader(file));
		    String text = null;
		    
			Long absoluteTime;
				
		    while ((text = reader.readLine()) != null) {
		    	Long timeStamp = Long.valueOf(text.split("\\s+")[1]);
		    	if (first) {
		    		baseTime = timeStamp;
		    		first = false;
		    	}
		    	absoluteTime = timeStamp - baseTime;
		    	if (debug) {
					System.out.println(absoluteTime);
				}
		    	dataMap.add(new DataPoint(absoluteTime, text));

			}
			reader.close();
		} catch (Exception e) {
		    e.printStackTrace();
		}
		
		createAndShowGUI();
	}
	
    private static void createAndShowGUI() {
        //Create and set up the window.
        JFrame frame = new JFrame("Log File Player");
        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        UI ui = new UI(dataMap);
        
        Thread controlTH = new Thread(ui);
        controlTH.start();

        //Add content to the window.
        frame.add(ui, BorderLayout.CENTER);

        //Display the window.
        frame.pack();
        frame.setVisible(true);
    }
	
	private static void parseCLI(String[] args) {
		Options options = new Options();

        Option logFilePathOption = new Option("f", "log_file", true, "path to log file");
        options.addOption(logFilePathOption);

        Option debugOption = new Option("d", "debug", false, "print debug");
        options.addOption(debugOption);

        CommandLineParser parser = new DefaultParser();
        HelpFormatter formatter = new HelpFormatter();
        CommandLine cmd;

        try {
            cmd = parser.parse(options, args);
        } catch (ParseException e) {
            System.out.println(e.getMessage());
            formatter.printHelp("Real Time FFT Grapher", options);

            System.exit(1);
            return;
        }

        logFilePath = cmd.getOptionValue("log_file");
		debug = cmd.hasOption("debug");
	}
}

